const startDate = new Date('2026-02-04'); 

const today = new Date();
const diffInMs = today - startDate;
const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24));
const currentStudyDay = diffInDays + 1;

return {
  studyDay: currentStudyDay, // This will now result in "1"
  isActive: currentStudyDay > 0 && currentStudyDay <= 60
};

